import React, {useState} from 'react'
import Blog from './Blog'
import blogService from '../services/blogs'

const BlogList = ({blogs}) => {
  const [blogToShow, setBlogToShow] = useState(null)

  const handleLike = (blogToLike) => async () => {
    const updatedBlog = await blogService.addLike(blogToLike)
    setBlogToShow(updatedBlog)
  }

  const handleView = (blog) => () => {
    setBlogToShow(blog)
  }

  const handleHide = () => {
    setBlogToShow(null)
  }

  const mapFunction = (id, blog, blogToShow) => {
    if (blog === blogToShow){
      return <Blog key={id} blog={blog} details={true} viewLabel='hide' viewFunction={handleHide} likeFunction={handleLike(blog)}/>
    } else {
      return <Blog key={id} blog={blog} details={false} viewLabel='view' viewFunction={handleView(blog)}/>
    }
  }
  return (
    <div>
      {blogs.map(blog => mapFunction(blog.id, blog, blogToShow))}
    </div>
  )
}

export default BlogList
