import React, {useState} from 'react'
import Blog from './Blog'
import blogService from '../services/blogs'

const BlogList = ({blogs}) => {
  const [blogToShow, setBlogToShow] = useState(null)

  const handleView = (blog) => () => {
    setBlogToShow(blog)
  }

  const handleHide = () => {
    setBlogToShow(null)
  }

  const handleDelete = (blogToDelete) => () => {
    if (window.confirm(`Remove blog ${blogToDelete.title} by ${blogToDelete.author}`)){
      blogService.deleteBlog(blogToDelete)
    }
  }

  const mapFunction = (id, blog, blogToShow) => {
    if (blog === blogToShow){
      return <Blog key={id} blog={blog} details={true} buttonLabel='hide' buttonFunction={handleHide} removeFunction={removeBlog({blog})}/>
    } else {
      return <Blog key={id} blog={blog} details={false} buttonLabel='view' buttonFunction={handleView(blog)}/>
    }
  }
  return (
    <div>
      {blogs
          .sort((blogA, blogB)=>blogB.likes - blogA.likes)
          .map(blog => mapFunction(blog.id, blog, blogToShow))
          }
    </div>
  )
}

export default BlogList
