import React, { useState } from 'react'
import Blog from './Blog'
import blogService from '../services/blogs'

const BlogList = ({ blogs, removeBlog, loggedInUser }) => {

  const [blogList, setBlogList] = useState(blogs)

  const like = (blog, blogList) => async () => {
    const updatedBlog = await blogService.addLike(blog)
    const newBlogs = blogs.map(b => {
      if (b===blog){
        return updatedBlog
      } else {
        return b
      }
    })
    setBlogList(newBlogs)
  }

  const mapFunction = (id, blog) => {
    return <Blog
        key={id}
        blog={blog}
        likeFunction = {like(blog)}
        removeFunction={removeBlog(blog)}
        loggedInUser={loggedInUser}/>

  }
  return (
    <div>
      {blogs
        .sort((blogA, blogB) => blogB.likes - blogA.likes)
        .map(blog => mapFunction(blog.id, blog))
      }
    </div>
  )
}

export default BlogList
