import React, { useState } from 'react'

const Blog = ({ blog, details, likeFunction, removeFunction, loggedInUser }) => {

  const [showDetails, setShowDetails] = useState(false)

  const showRemoveButton = { display: blog.user.username === loggedInUser.username ? '': 'none' }

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }

  const handleShowDetails = () => {
    setShowDetails(!showDetails)
  }


  if (showDetails){
    return (
      <div style={blogStyle} className="blogDetailed">
        <p>{blog.title} {blog.author} <button onClick={handleShowDetails}>hide</button> </p>
        <p>{blog.url}</p>
        <p>likes {blogState.likes} <button onClick={likeFunction}>like</button></p>
        <p>{blog.user.name}</p>
        <button onClick={removeFunction} style={showRemoveButton} >remove</button>
      </div>
    )
  } else {
    return (
      <div style={blogStyle} className="blogNotDetailed">
        {blog.title} {blog.author} <button onClick={handleShowDetails}>view</button>
      </div>
    )
  }

}
export default Blog
