import React, { useState, useEffect } from 'react'
import Blog from './components/Blog'
import Notification from './components/Notification'
import Togglable from './components/Togglable'
import BlogForm from './components/BlogForm'
import blogService from './services/blogs'
import loginService from './services/login'
import './index.css'

const App = () => {
  const [blogs, setBlogs] = useState([])
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [user, setUser] = useState(null)
  const [title, setTitle] = useState('')
  const [author, setAuthor] = useState('')
  const [url, setUrl] = useState('')
  const [errorMessage, setErrorMessage] = useState(null)
  const [errorColor, setErrorColor] = useState(null)

  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs( blogs )
    )
  }, [])

  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
    if(loggedUserJSON){
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
    }
  }, [])

  const handleLogout = () => {
    window.localStorage.removeItem('loggedBlogappUser')
    setUser(null)
  }

  const handleLogin = async (event) => {
    event.preventDefault()
    try {
      const user = await loginService.login({username, password})
      window.localStorage.setItem(
        'loggedBlogappUser', JSON.stringify(user)
      )
      blogService.setToken(user.token)
      setUser(user)
      setUsername('')
      setPassword('')
      setErrorMessage('Log in successful')
      setErrorColor('green')
      setTimeout(()=>{
        setErrorMessage(null)
        setErrorColor(null)
      },5000)
    } catch(exception){
      setErrorMessage('wrong username or password')
      setErrorColor('red')
      setTimeout(()=>{
        setErrorMessage(null)
        setErrorColor(null)
      },5000)
    }
  }

  const createBlog = async (event) => {
    event.preventDefault()
    try {
      const blog = await blogService.addBlog({title, author, url})
      setBlogs(blogs.concat(blog))
      setErrorMessage(`a new blog added: ${title} from ${author}`)
      setErrorColor('green')
      setTimeout(()=>{
        setErrorMessage(null)
        setErrorColor(null)
      },5000)
    } catch(exception){
      setErrorMessage('Blog details not complete')
      setErrorColor('red')
      setTimeout(()=>{
        setErrorMessage(null)
        setErrorColor(null)
      },5000)
    }
  }

  const loginForm = () => (
    <div>
    <h1>log in to application</h1>
    <Notification message={errorMessage} color={errorColor}/>
    <form onSubmit={handleLogin}>
      <div>
      username<input
        type="text"
        value={username}
        name="Username"
        onChange={({target}) => setUsername(target.value)}/>
      </div>
      <div>
      password<input
        type="text"
        value={password}
        name="Password"
        onChange={({target}) => setPassword(target.value)}/>
      </div>
      <button type="submit">login</button>
    </form>
    </div>
  )


  const blogForm = () => (
    <div>
     <h2>blogs</h2>
     <Notification message={errorMessage} color={errorColor}/>
     <p>{user.name} logged in <button onClick={handleLogout}>logout</button></p>
     <h2>create new</h2>
     <Togglable buttonLabel="new blog">
      <BlogForm
        onSubmit={createBlog}
        handleChangeTitle={(event)=>setTitle(event.target.value)}
        handleChangeAuthor={(event)=>setAuthor(event.target.value)}
        handleChangeUrl={(event)=>setUrl(event.target.value)}
        title={title}
        author={author}
        url={url}
      />
      </Togglable>
     {blogs.map(blog =>
       <Blog key={blog.id} blog={blog} />
     )}
   </div>
  )


  return (
    <div>
    {user === null ? loginForm() : blogForm()}
    </div>

  )

}

export default App
