import React, {useState} from 'react'
import blogService from '../services/blogs'

const Blog = ({ blog, details, buttonLabel, buttonFunction, removeFunction, loggedInUser}) => {

  const [blogState, setBlogState] = useState(blog)
  console.log(loggedInUser)
  const showRemoveButton = {display: blog.user === loggedInUser ? '': 'none'}

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }


  const like = (blogState) => async () => {
    const updatedBlog = await blogService.addLike(blogState)
    setBlogState(updatedBlog)
  }

  if (details){
    return (
    <div style={blogStyle}>
      <p>{blog.title} {blog.author} <button onClick={buttonFunction}>{buttonLabel}</button> </p>
      <p>{blog.url}</p>
      <p>likes {blogState.likes} <button onClick={like(blogState)}>like</button></p>
      <p>{blog.user.name}</p>
      <button onClick={removeFunction} style={showRemoveButton} >remove</button>
    </div>
    )
  }
  return (
    <div style={blogStyle}>
      {blog.title} {blog.author} <button onClick={buttonFunction}>{buttonLabel}</button>
    </div>
  )
}
export default Blog
