import React, {useState} from 'react'
import blogService from '../services/blogs'

const Blog = ({ blog, details, buttonLabel, viewFunction, likeFunction }) => {

  const [likes, setLikes] = useState(blog.likes)

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }


  if (details){
    return (
    <div style={blogStyle}>
      <p>{blog.title} {blog.author} <button onClick={buttonFunction}>{buttonLabel}</button> </p>
      <p>{blog.url}</p>
      <p>likes {likes} <button onClick={like(blog)}>like</button></p>
      <p>{blog.user.name}</p>
    </div>
    )
  }
  return (
    <div style={blogStyle}>
      {blog.title} {blog.author} <button onClick={buttonFunction}>{buttonLabel}</button>
    </div>
  )
}
export default Blog
