import React, {useState} from 'react'
import Blog from './Blog'

const BlogList = ({blogs, removeBlog}) => {
  const [blogToShow, setBlogToShow] = useState(null)

  const handleView = (blog) => () => {
    setBlogToShow(blog)
  }

  const handleHide = () => {
    setBlogToShow(null)
  }

  const mapFunction = (id, blog, blogToShow) => {
    if (blog === blogToShow){
      return <Blog key={id} blog={blog} details={true} buttonLabel='hide' buttonFunction={handleHide}/>
    } else {
      return <Blog key={id} blog={blog} details={false} buttonLabel='view' buttonFunction={handleView(blog)}/>
    }
  }
  return (
    <div>
      {blogs
          .sort((blogA, blogB)=>blogB.likes - blogA.likes)
          .map(blog => mapFunction(blog.id, blog, blogToShow))
          }
    </div>
  )
}

export default BlogList
