    <div>
      {blogs.map(blog => {
        if (blog === blogToShow){
          return <Blog key={blog.id} blog={blog} view='false' buttonLabel='hide' buttonFunction={handleHide}/>
        } else {
          return <Blog key={blog.id} blog={blog} view='true' buttonLabel='view' buttonFunction={handleView(blog)}/>
